  /**
   * Reset counter to 0
   * @see {SigningCosmWasmClient}
   * @see https://github.com/drewstaylor/archway-template/blob/main/src/contract.rs#L43
   */
  resetCounter = async () => {
    // SigningCosmWasmClient.execute: async (senderAddress, contractAddress, msg, fee, memo = "", funds)
    if (!this.state.accounts) {
      console.warn('Error getting user account', this.state.accounts);
      return;
    } else if (!this.state.userAddress) {
      console.warn('Error getting user address', this.state.userAddress);
      return;
    }
    let loading;
    loading = {
      status: true,
      msg: "Resetting counter..."
    };
    this.setState({
      loadingStatus: loading.status,
      loadingMsg: loading.msg
    });
    // Prepare Tx
    let entrypoint = {
      reset: {
        count: 1
      }
    };
    let txFee = calculateFee(300000, this.state.gasPrice); // XXX TODO: Fix gas estimation (https://github.com/cosmos/cosmjs/issues/828)
    // Send Tx
    try {
      let tx = await this.state.cwClient.execute(this.state.userAddress, this.state.contract, entrypoint, txFee);
      console.log('Reset Tx', tx);
      // Update Logs
      if (tx.logs) {
        if (tx.logs.length) {
          tx.logs[0].type = 'reset';
          tx.logs[0].timestamp = new Date().getTime();
          this.setState({
            logs: [JSON.stringify(tx.logs, null, 2), ...this.state.logs]
          });
        }
      }
      // Refresh counter
      let counter = await this.getCount();
      let count;
      if (!isNaN(counter.count)) {
        count = counter.count;
      } else {
        count = this.state.counter;
        console.warn('Error expected numeric value from counter, found: ', typeof counter.count);
      }
      // Render updates
      loading = {
        status: false,
        msg: ""
      };
      this.setState({
        counter: count,
        loadingStatus: loading.status,
        loadingMsg: loading.msg
      });
    } catch (e) {
      console.warn('Error executing Reset', e);
      loading = {
        status: false,
        msg: ""
      };
      this.setState({
        loadingStatus: loading.status,
        loadingMsg: loading.msg
      });
    }
  }

  render() {
    // State
    const counter = this.state.counter;
    const loadingMsg = this.state.loadingMsg;
    const userAddress = this.state.userAddress;

    // Maps
    let logMeta = [];
    for (let i = 0; i < this.state.logs.length; i++) {
      let logItem = JSON.parse(this.state.logs[i]);
      let meta = {
        type: logItem[0].type,
        timestamp: logItem[0].timestamp
      };
      logMeta.push(meta);
    }
    const logItems = (this.state.logs.length) ? this.state.logs.map((log,i) =>
      <div key={logMeta[i].timestamp}>
        <p className="label">
          <strong><span>Trend {(logMeta[i].type === 'increment') ? 'UP' : 'Reset' }&nbsp;</span>({logMeta[i].timestamp}):</strong>
        </p>
        <pre className="log-entry" key={i}>{log}</pre>
      </div>
    ) : null;

    // Not Connected
    if (!userAddress) {
      return (
        <div className="content">
          <img src={logo} alt="logo" />

          <div className="button-controls">
            <button id="connect" className="btn btn-main" onClick={this.connectWallet}>Connect Wallet</button>
          </div>

        </div>
      );
    }

    // Connected
	if (counter > 0){
	var upcounter = "MOOON"
	}
    return (
      <div className="content">
        <img src={logo} alt="logo" />
	<div  style="color:#ffff00" align="center">We are going: </div>
        {/* Counter Status Display */}
        <div className="status-display">
          <ul className="status">
           <li className="counter"><strong>Trend:</strong>&nbsp;{upcounter}</li>
          </ul>
	<strong><small><p>If trend {'>'} 0 now is Uptrand ðŸ“ˆ</p></small></strong>
	<strong><small><p>If trend {'< 0'} now is Downtrend ðŸ“‰</p></small></strong>
	</div>

        {/* Controls */}
        <div className="button-controls">
         <button id="incrementer" className="btn btn-main" onClick={this.incrementCounter}>Uptrend</button>
	<button id="incrementerbelow" className="btn btn-main" onClick={this.incrementCounterDown}>Downtrend</button>
        </div>

        {/* Loading */}
       {Loading(loadingMsg)}

        {/* Logs map */}
        <div className="logs">
          <div>{logItems}</div>
        </div>

      </div>
    );
  };

}

// Conditional rendering
function Loading(msg) {
  if (!msg) {
    return;
  }
  return (
    <div className="loading">
      <p>Changing Trend...</p>
    </div>
  );
}
